zsh_additions.content:
set_prompt() {
  USER_NAME="%F{red}$(whoami)%f"
  BASEDIR=${PWD##*/}
  SUBDIR=${PWD%/*}
  SUBDIR=${SUBDIR##*/}
  PS1="$USER_NAME@%F{green}$SUBDIR/%F{blue}$BASEDIR %f\$ "
}
precmd_functions+=(set_prompt)

# Kitty session time display
if [[ "$TERM" == "xterm-kitty" ]]; then
  # Store terminal start time
  export KITTY_SESSION_START=${KITTY_SESSION_START:-$(date +%s)}

  # Function to get session time info
  get_kitty_session_info() {
    local current_time=$(date +%s)
    local session_duration=$((current_time - KITTY_SESSION_START))

    echo "[Session: ${session_duration}s]"
  }

  # Enhanced prompt for Kitty with session info
  kitty_set_prompt() {
    local session_info="%F{yellow}$(get_kitty_session_info)%f"
    USER_NAME="%F{red}$(whoami)%f"
    BASEDIR=${PWD##*/}
    SUBDIR=${PWD%/*}
    SUBDIR=${SUBDIR##*/}
    PS1="$session_info\n$USER_NAME@%F{green}$SUBDIR/%F{blue}$BASEDIR %f\$ "
  }

  # Override the default prompt function for Kitty
  precmd_functions=(kitty_set_prompt)
fi
